import React from "react";
import { render, fireEvent } from "@testing-library/react-native";
import { ImageSelect } from "./ImageSelect";
import * as ImagePicker from "expo-image-picker";
import { uploadImage } from "../api/service";

const successfulImageUploadResponse = {
  filename: "meal.jpg",
  file_path: "/tmp/uploads/meal3.jpg",
  label: "dinner",
};

const mockImage = {
  uri: "data:image/jpeg;base64,",
  fileName: "photo.jpg",
};

jest.mock("expo-image-picker");

jest.mock("../api/service", () => ({
  uploadImage: jest.fn(),
}));

describe("Image Select", () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  test("Modal opens after image selection", async () => {
    const mockImage = {
      uri: "data:image/jpeg;base64,",
      fileName: "photo.jpg",
    };

    ImagePicker.launchImageLibraryAsync.mockResolvedValueOnce({
      canceled: false,
      assets: [mockImage],
    });

    const { getByTestId, findByText } = render(<ImageSelect />);

    // click on select image
    fireEvent.press(getByTestId("image-select-button"));

    const confirmText = await findByText("Confirm");

    expect(confirmText).toBeTruthy();
  });

  test("Success Modal appears after successful image upload", async () => {
    ImagePicker.launchImageLibraryAsync.mockResolvedValueOnce({
      canceled: false,
      assets: [mockImage],
    });

    uploadImage.mockResolvedValueOnce({
      status: 200,
      json: jest.fn().mockResolvedValueOnce(successfulImageUploadResponse),
    });

    const { getByTestId, findByText } = render(<ImageSelect />);

    // click on select image
    fireEvent.press(getByTestId("image-select-button"));

    const confirmText = await findByText("Confirm");

    // click on confirm image upload
    fireEvent.press(confirmText);

    const successMessage = await findByText("Success!");

    expect(successMessage).toBeTruthy();
  });
});
